SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS regra_numeracao_decla_befins ON regra_numeracao_declaracao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_regra_numeracao_decla_befins() RETURNS trigger AS $BODY$
declare
retorno_w bigint;

PRAGMA AUTONOMOUS_TRANSACTION;

BEGIN

select count(*)
into STRICT retorno_w
from REGRA_NUMERACAO_DEC_ITEM x
where NR_DECLARACAO >= NEW.NR_INICIAL
and NR_DECLARACAO <= NEW.NR_FINAL
and (SELECT IE_TIPO_NUMERACAO 
		FROM REGRA_NUMERACAO_DEC_ITEM R, 
			REGRA_NUMERACAO_DECLARACAO RN 
		WHERE R.NR_SEQ_REGRA_NUM = RN.NR_SEQUENCIA
        AND R.NR_SEQUENCIA = X.NR_SEQUENCIA ) = NEW.IE_TIPO_NUMERACAO;

if (retorno_w > 0) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(1092632);
end if;


RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_regra_numeracao_decla_befins() FROM PUBLIC;

CREATE TRIGGER regra_numeracao_decla_befins
	BEFORE INSERT OR UPDATE ON regra_numeracao_declaracao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_regra_numeracao_decla_befins();

