SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS regra_perm_panorama_update ON regra_perm_panorama CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_regra_perm_panorama_update() RETURNS trigger AS $BODY$
declare
qt_registros_w	bigint;

BEGIN
if (OLD.nr_modelo_suep <> NEW.nr_modelo_suep) then
	select	count(1)
	into STRICT	qt_registros_w
	from	regra_perm_pan_icone_suep a,
			regra_perm_panorama_icone b
	where	a.nr_icone_modelo = b.nr_sequencia
	and		b.nr_seq_regra = NEW.nr_sequencia;

	if (qt_registros_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1039653);
		--'Não é possível alterar o modelo do SUEP da regra atual, pois há icones que possuem cards vinculados para este modelo! Para que seja possível alterar o modelo, deve-se excluir dos ícones os cards liberados do modelo anterior, ou criar uma nova regra com o modelo desejado.'
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_regra_perm_panorama_update() FROM PUBLIC;

CREATE TRIGGER regra_perm_panorama_update
	BEFORE UPDATE ON regra_perm_panorama FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_regra_perm_panorama_update();

