DROP TRIGGER IF EXISTS reg_lic_compra_item_delete ON reg_lic_compra_item CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_reg_lic_compra_item_delete() RETURNS trigger AS $BODY$
declare

qt_liberado_w	bigint := 0;
ds_chave_w	varchar(255);
nm_usuario_w	varchar(15);

BEGIN
nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

ds_chave_w :=	substr('NR_SEQUENCIA='||OLD.nr_sequencia||',NR_SEQ_REG_LIC_COMPRA='||OLD.nr_seq_reg_lic_compra,1,255);

select	count(*)
into STRICT	qt_liberado_w
from	reg_lic_compra
where	nr_sequencia =OLD.nr_seq_reg_lic_compra
and	dt_liberacao is not null;

if (qt_liberado_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(357805);
end if;

ds_chave_w := substr(WHEB_MENSAGEM_PCK.get_texto(799553)||'='||WHEB_MENSAGEM_PCK.get_texto(799554)||','||ds_chave_w,1,255);

insert into log_exclusao(ds_chave,
	dt_atualizacao,
	nm_tabela,
	nm_usuario)
values (ds_chave_w,
	LOCALTIMESTAMP,
	'REG_LIC_COMPRA_ITEM',
	nm_usuario_w);


RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
