DROP TRIGGER IF EXISTS reg_product_requirement_insert ON reg_product_requirement CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_reg_product_requirement_insert() RETURNS trigger AS $BODY$
DECLARE
	dt_aprovacao_w reg_customer_requirement.dt_aprovacao%TYPE;
BEGIN 
	SELECT	dt_aprovacao 
	INTO STRICT	dt_aprovacao_w 
	FROM	reg_customer_requirement 
	WHERE	nr_sequencia = NEW.nr_customer_requirement;
	 
	IF	dt_aprovacao_w IS NULL THEN 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(901103);--Não é possivel inserir um requisito de produto em um requisito de cliente não aprovado 
	END IF;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
