DROP TRIGGER IF EXISTS repasse_terceiro_item_delete ON repasse_terceiro_item CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_repasse_terceiro_item_delete() RETURNS trigger AS $BODY$
declare
ie_ctb_online_w         varchar(01);
cd_estabelecimento_w    estabelecimento.cd_estabelecimento%type;
cd_tipo_lote_contabil_w CONSTANT smallint := 14;
nm_tabela_w             CONSTANT varchar(21) := upper('REPASSE_TERCEIRO_ITEM');
ie_situacao_ctb_w         varchar(01);

BEGIN
if (coalesce(wheb_usuario_pck.get_ie_executar_trigger, 'S') = 'S') then
    BEGIN
    select  t.cd_estabelecimento
    into STRICT    cd_estabelecimento_w
    from    terceiro t
    where   t.nr_sequencia = OLD.nr_seq_terceiro;

    select  coalesce(max(ie_situacao_ctb),'P')
    into STRICT    ie_situacao_ctb_w
    from    ctb_documento a
    where   a.cd_tipo_lote_contabil = cd_tipo_lote_contabil_w
    and     a.nm_tabela = nm_tabela_w
    and     a.nr_documento = OLD.nr_sequencia;

    -- P (Pendente)
    -- C (Contabilizado)
    -- N (Normal)
    if (ie_situacao_ctb_w = 'C') then
        --if (nvl(:old.nr_lote_contabil,0) <> 0 or nvl(:old.nr_lote_contabil_prov,0) <> 0) then
        --ie_ctb_online_w := ctb_online_pck.get_modo_lote(cd_tipo_lote_contabil_p => cd_tipo_lote_contabil_w,
                                                        --cd_estabelecimento_p    => cd_estabelecimento_w);
       -- if (ie_ctb_online_w = upper('S')) then
            CALL ctb_contab_onl_repasse_pck.deleta_contab_item_repasse(nr_sequencia_p => OLD.nr_sequencia,
                                                                  nm_usuario_p   => coalesce(wheb_usuario_pck.get_nm_usuario,OLD.nm_usuario),
                                                                  ie_commit_p    => upper('N'));
    end if;

    delete  FROM ctb_documento a
    where   a.cd_tipo_lote_contabil = cd_tipo_lote_contabil_w
    and     a.nm_tabela = nm_tabela_w
    and     a.nr_documento = OLD.nr_sequencia;
    end;
end if;

RETURN OLD;
end
$BODY$
