CREATE OR REPLACE FUNCTION trigger_fct_repasse_terc_venc_trib_insert() RETURNS trigger AS $BODY$
DECLARE
ds_tributo_w 	varchar(255);

BEGIN

if (NEW.vl_imposto < 0) then
	select	ds_tributo
	into STRICT	ds_tributo_w
	from	tributo
	where	cd_tributo	= NEW.cd_tributo;

	/* O valor do tributo #@DS_TRIBUTO nÃ£o pode ser negativo!
	Vl_tributo = #@VL_TRIBUTO#@ */
	CALL wheb_mensagem_pck.exibir_mensagem_abort(266955, 'DS_TRIBUTO=' || ds_tributo_w || ';VL_TRIBUTO=' || NEW.vl_imposto);
end if;

RETURN NEW;
END
