CREATE OR REPLACE FUNCTION trigger_fct_rep_diretorio_rel_insert() RETURNS trigger AS $BODY$
declare
ie_classif_WATE_w varchar(1);
 
BEGIN 
select CASE WHEN count(cd_classif_relat)=1 THEN 'S'  ELSE 'N' END  
into STRICT	ie_classif_WATE_w 
from 	relatorio 
where  nr_sequencia = NEW.nr_seq_relatorio 
and 	cd_classif_relat = 'WATE';
 
if (ie_classif_WATE_w = 'S') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(obter_texto_dic_objeto(691300, wheb_usuario_pck.get_nr_seq_idioma, null));
end if;
 
RETURN NEW;
end
