DROP TRIGGER IF EXISTS rep_horario_hor_pac_update ON rep_horario_hor_pac CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_rep_horario_hor_pac_update() RETURNS trigger AS $BODY$
DECLARE
 
ds_erro_w			varchar(2000);
ie_atualiza_cpoe_p	varchar(1);
pragma autonomous_transaction;
BEGIN
  BEGIN 
 
BEGIN 
 
if (OLD.dt_cancelamento is null AND NEW.dt_cancelamento is not null) then 
	select 	coalesce(max('N'), 'S') 
	into STRICT	ie_atualiza_cpoe_p 
	from	rep_horario_hor_pac 
	where	dt_cancelamento is null 
	and		nr_sequencia <> NEW.nr_sequencia 
	and		dt_definicao > NEW.dt_definicao;
	 
	if (ie_atualiza_cpoe_p = 'S') then 
		CALL cpoe_ajustar_horarios_padroes(NEW.nr_atendimento, NEW.cd_material, NEW.nr_seq_proc_interno, NEW.cd_intervalo, null, NEW.nm_usuario, NEW.nr_seq_solucao, NEW.nr_prescricao);
	end if;
elsif (OLD.ds_horario <> NEW.ds_horario) then 
	CALL cpoe_ajustar_horarios_padroes(NEW.nr_atendimento, NEW.cd_material, NEW.nr_seq_proc_interno, NEW.cd_intervalo, NEW.ds_horario, NEW.nm_usuario, NEW.nr_seq_solucao, NEW.nr_prescricao);
end if;
 
exception 
when others then 
	ds_erro_w := null;
end;
 
  END;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
