DROP TRIGGER IF EXISTS rep_jejum_insert ON rep_jejum CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_rep_jejum_insert() RETURNS trigger AS $BODY$
DECLARE

dt_inicio_prescr_w	timestamp;
dt_validade_prescr_w	timestamp;

BEGIN

if (NEW.dt_inicio is null) then

	select	max(dt_inicio_prescr),
		max(dt_validade_prescr)
	into STRICT	dt_inicio_prescr_w,
		dt_validade_prescr_w
	from	prescr_medica
	where	nr_prescricao = NEW.nr_prescricao;

	NEW.dt_inicio	:= dt_inicio_prescr_w;
	--:new.dt_fim	:= dt_validade_prescr_w;
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_rep_jejum_insert() FROM PUBLIC;

CREATE TRIGGER rep_jejum_insert
	BEFORE INSERT ON rep_jejum FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_rep_jejum_insert();

