DROP TRIGGER IF EXISTS result_laboratorio_pdf_insert ON result_laboratorio_pdf CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_result_laboratorio_pdf_insert() RETURNS trigger AS $BODY$
declare
ie_status_atend_w		prescr_procedimento.ie_status_atend%type;
ie_suspenso_w			prescr_procedimento.ie_suspenso%type;
total_err_w			smallint;
qt_reg_w			smallint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'N') then
	Goto Final;
end if;

if (wheb_usuario_pck.is_evento_ativo(766) = 'S') then

	select a.ie_status_atend, ie_suspenso
		into STRICT ie_status_atend_w, ie_suspenso_w
	from prescr_procedimento a
		where a.nr_sequencia = NEW.nr_seq_prescricao
		and a.nr_prescricao = NEW.nr_prescricao;

	if (ie_status_atend_w >= 35) and (ie_suspenso_w <> 'S') then
		CALL integrar_unimed_rs_ws(766, NEW.nr_prescricao, NEW.nr_seq_prescricao , NEW.nm_usuario, 'Registro');
	end if;
end if;

<<Final>>
qt_reg_w	:= 0;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
