SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS rxt_agenda_update ON rxt_agenda CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_rxt_agenda_update() RETURNS trigger AS $BODY$
DECLARE
 
nr_sessao_w		bigint;
cd_perfil_w		integer;
cd_pessoa_fisica_w	varchar(10);
qt_reg_w	smallint;
BEGIN 
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N') then 
	goto Final;
end if;
select	coalesce(max(nr_sessao),0), 
	coalesce(max(cd_perfil),0) 
into STRICT	nr_sessao_w, 
	cd_perfil_w 
from	rxt_parametro;
 
if (NEW.ie_status_agenda = 'E') and (NEW.nr_seq_dia = nr_sessao_w) and (nr_sessao_w > 0) then 
	BEGIN 
	if (cd_perfil_w > 0) then 
		BEGIN 
		 
		/* Obter dados paciente */
 
		select	coalesce(rxt_obter_pf_tratamento(NEW.nr_seq_tratamento,null),NEW.cd_pessoa_fisica) 
		into STRICT	cd_pessoa_fisica_w			 
		;		
		 
		if (cd_pessoa_fisica_w is not null) then 
			insert into comunic_interna( 
				dt_comunicado, 
				ds_titulo, 
				ds_comunicado, 
				nm_usuario, 
				dt_atualizacao, 
				ie_geral, 
				nm_usuario_destino, 
				ds_perfil_adicional, 
				nr_sequencia, 
				ie_gerencial, 
				dt_liberacao 
			) values ( 
				LOCALTIMESTAMP, 
				wheb_mensagem_pck.get_texto(799247), 
				wheb_mensagem_pck.get_texto(799241) || ' ' || cd_pessoa_fisica_w ||' - '|| obter_nome_pf(cd_pessoa_fisica_w) || chr(13) || chr(10) || wheb_mensagem_pck.get_texto(799252), 
				NEW.nm_usuario, 
				LOCALTIMESTAMP, 
				'N', 
				'', 
				cd_perfil_w ||', ', 
				nextval('comunic_interna_seq'), 
				'N', 
				LOCALTIMESTAMP);
		end if;
		end;
	end if;
	end;
end if;	
<<Final>> 
qt_reg_w	:= 0;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_rxt_agenda_update() FROM PUBLIC;

CREATE TRIGGER rxt_agenda_update
	BEFORE UPDATE ON rxt_agenda FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_rxt_agenda_update();

