SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS san_exame_lote_update ON san_exame_lote CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_san_exame_lote_update() RETURNS trigger AS $BODY$
declare
nr_atendimento_w	bigint;
ie_status_w		varchar(1);
qt_reg_w	smallint;
BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;
if (OLD.nr_seq_transfusao is not null) and (NEW.nr_seq_transfusao is null) then

	select ie_status
	into STRICT	ie_status_w
	from	san_transfusao
	where	nr_sequencia = OLD.nr_seq_transfusao;

	if (NEW.nr_seq_reserva is not null) then
		select	coalesce(max(nr_atendimento),0)
		into STRICT	nr_atendimento_w
		from san_reserva
		where nr_sequencia = NEW.nr_seq_reserva;
	end if;

	if (ie_status_w = 'F') and
		((NEW.nr_seq_reserva is null) or (nr_atendimento_w > 0)) then
		update san_exame_realizado
		set nr_seq_propaci  = NULL
		where nr_seq_exame_lote = NEW.nr_sequencia
		  and nr_seq_propaci is not null;
	end if;
end if;
<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
