CREATE OR REPLACE FUNCTION trigger_fct_san_reserva_prod_update() RETURNS trigger AS $BODY$
declare
IE_RESERVA_NAO_UTILIZADA_W	varchar(1);
qt_procedimento_w		integer;
BEGIN

select 	max(coalesce(IE_RESERVA_NAO_UTILIZADA,'N'))
into STRICT 	IE_RESERVA_NAO_UTILIZADA_w
from 	san_parametro;

if (NEW.ie_status = 'N') and (NEW.ie_status <> OLD.ie_status) and (IE_RESERVA_NAO_UTILIZADA_w <> 'N')then

	NEW.nr_seq_propaci := null;

	if (IE_RESERVA_NAO_UTILIZADA_w = 'C') and (OLD.nr_seq_propaci is not null) then

		select coalesce(max(qt_procedimento),0)
		into STRICT	qt_procedimento_w
		from procedimento_paciente
		where nr_sequencia = OLD.nr_seq_propaci;

		if (qt_procedimento_w > 1) then
			update procedimento_paciente
			set qt_procedimento = qt_procedimento - 1
			where nr_sequencia = OLD.nr_seq_propaci;

			update procedimento_paciente
			set qt_procedimento = qt_procedimento - 1
			where nr_seq_proc_princ = OLD.nr_seq_propaci;

			update material_atend_paciente
			set qt_material = qt_material - 1
			where nr_seq_proc_princ = OLD.nr_seq_propaci;
		else
			delete FROM procedimento_paciente
			where nr_seq_proc_princ = OLD.nr_seq_propaci;

			delete FROM material_atend_paciente
			where nr_seq_proc_princ = OLD.nr_seq_propaci;

			delete FROM procedimento_paciente
			where nr_sequencia = OLD.nr_seq_propaci;
		end if;
	end if;
end if;

if (NEW.ie_status <> 'N') and (OLD.nr_seq_propaci is not null) and (NEW.nr_seq_propaci is null) then

	select coalesce(max(qt_procedimento),0)
	into STRICT	qt_procedimento_w
	from procedimento_paciente
	where nr_sequencia = OLD.nr_seq_propaci;

	if (qt_procedimento_w > 1) then
		update procedimento_paciente
		set qt_procedimento = qt_procedimento - 1
		where nr_sequencia = OLD.nr_seq_propaci;

		update procedimento_paciente
		set qt_procedimento = qt_procedimento - 1
		where nr_seq_proc_princ = OLD.nr_seq_propaci;

		update material_atend_paciente
		set qt_material = qt_material - 1
		where nr_seq_proc_princ = OLD.nr_seq_propaci;
	else
		delete FROM procedimento_paciente
		where nr_seq_proc_princ = OLD.nr_seq_propaci;

		delete FROM material_atend_paciente
		where nr_seq_proc_princ = OLD.nr_seq_propaci;

		delete FROM procedimento_paciente
		where nr_sequencia = OLD.nr_seq_propaci;
	end if;
end if;
RETURN NEW;
end
