SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS setor_atendimento_update ON setor_atendimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_setor_atendimento_update() RETURNS trigger AS $BODY$
Declare
dt_inicio_prescricao_w	varchar(100) := '30/12/1899';

ds_origem_w	varchar(1800);
BEGIN
if (NEW.IE_SITUACAO = 'I')  AND (OLD.IE_SITUACAO = 'A') then

	NEW.IE_OCUP_HOSPITALAR := 'N';

end if;

if (NEW.IE_SITUACAO = 'I')  then
	NEW.dt_inativacao :=	 LOCALTIMESTAMP;
elsif (NEW.IE_SITUACAO = 'A')  AND (OLD.IE_SITUACAO = 'I') then
	NEW.dt_inativacao := null;
end if;

if (NEW.dt_inativacao <> OLD.dt_inativacao) and (to_char(NEW.dt_inativacao,'dd/mm/yyyy hh24:mi:ss') = '00/00/0000 00:00:00') then

	ds_origem_w	:= substr(dbms_utility.format_call_stack,1,1800);

	insert into log_mov(	DT_ATUALIZACAO,
				NM_USUARIO,
				CD_LOG,
				DS_LOG)
		values (	LOCALTIMESTAMP,
				NEW.nm_usuario,
				55838,
				' Stack: '|| ds_origem_w
				);
end if;

if (NEW.ie_adep <> OLD.ie_adep) then

	ds_origem_w	:= substr(dbms_utility.format_call_stack,1,1800);

	insert into log_mov(	DT_ATUALIZACAO,
				NM_USUARIO,
				CD_LOG,
				DS_LOG)
		values (	LOCALTIMESTAMP,
				NEW.nm_usuario,
				55899,
				substr('Old: '||OLD.ie_adep||'/ New: '||NEW.ie_adep||'/ Stack: '|| ds_origem_w,1,4000)
				);
end if;

if (NEW.hr_inicio_prescricao is not null) and (NEW.hr_inicio_prescricao > to_date('01/01/2013 00:00:01','dd/mm/yyyy hh24:mi:ss')) then
	-- Tratamento feito, pois o componente no delphi é colocado data padrão abaixo do ano 1900, e no java pega a atual -- Richart
	NEW.hr_inicio_prescricao := to_date(dt_inicio_prescricao_w||' '||to_char(NEW.hr_inicio_prescricao,'hh24:mi;ss'),'dd/mm/yyyy hh24:mi:ss');
end if;


RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_setor_atendimento_update() FROM PUBLIC;

CREATE TRIGGER setor_atendimento_update
	BEFORE UPDATE ON setor_atendimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_setor_atendimento_update();

