DROP TRIGGER IF EXISTS smart_atendimento_pac_alta ON atendimento_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_smart_atendimento_pac_alta() RETURNS trigger AS $BODY$
declare
reg_integracao_p		gerar_int_padrao.reg_integracao;
nr_seq_interno_w		bigint;
atend_atepacu_w			atendimento_paciente.nr_Atendimento%type;
	
BEGIN
if (TG_OP = 'UPDATE') and (NEW.dt_cancelamento is null) and
	((NEW.dt_saida_real is not null AND OLD.dt_saida_real is null) or (NEW.dt_saida_real is null AND OLD.dt_saida_real is not null)) then
	
	if (TG_OP = 'UPDATE') and (NEW.dt_saida_real is not null) and (OLD.dt_saida_real is null) then
		reg_integracao_p.ie_operacao		:=	'A';
	elsif (TG_OP = 'UPDATE') and (NEW.dt_saida_real is null) and (OLD.dt_saida_real is not null) then
		reg_integracao_p.ie_operacao		:=	'E';
	end if;

	reg_integracao_p.nr_atendimento			:=	NEW.nr_atendimento;
	reg_integracao_p.cd_pessoa_fisica		:=	NEW.cd_pessoa_fisica;
	reg_integracao_p.ie_tipo_atendimento		:= 	NEW.ie_tipo_atendimento;
	reg_integracao_p.nr_seq_tipo_admissao_fat	:= 	NEW.nr_seq_tipo_admissao_fat;
	reg_integracao_p.cd_motivo_alta			:= 	NEW.cd_motivo_alta;
	
	select obter_atepacu_paciente(NEW.nr_Atendimento,'IAA')
	into STRICT atend_atepacu_w
	;
	
	if (atend_atepacu_w <> 0) then
		reg_integracao_p := gerar_int_padrao.gravar_integracao('114', NEW.nr_atendimento, coalesce(obter_usuario_ativo, NEW.nm_usuario), reg_integracao_p);
	end if;
elsif (TG_OP = 'UPDATE') and ((OLD.dt_cancelamento is null AND NEW.dt_cancelamento is not null) or (coalesce(OLD.cd_medico_referido,'0') <> coalesce(NEW.cd_medico_referido,'0')) or (coalesce(OLD.cd_medico_resp,'0') <> coalesce(NEW.cd_medico_resp,'0'))) then
	
	select obter_atepacu_paciente(NEW.nr_atendimento,'IAA')
	into STRICT	nr_seq_interno_w
	;
	if (nr_seq_interno_w <> 0) then
		reg_integracao_p.ie_operacao				:=	'A';
		reg_integracao_p.nr_atendimento				:=	NEW.nr_atendimento;
		reg_integracao_p.cd_pessoa_fisica			:=	NEW.cd_pessoa_fisica;
		reg_integracao_p.ie_tipo_atendimento		:= 	NEW.ie_tipo_atendimento;
		reg_integracao_p.nr_seq_tipo_admissao_fat	:= 	NEW.nr_seq_tipo_admissao_fat;	
		reg_integracao_p.cd_motivo_alta			:= 	NEW.cd_motivo_alta;
		
		reg_integracao_p := gerar_int_padrao.gravar_integracao('308', nr_seq_interno_w, coalesce(obter_usuario_ativo, NEW.nm_usuario), reg_integracao_p);	
	end if;
	
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
