declare
	reg_integracao_w		gerar_int_padrao.reg_integracao;
BEGIN
  BEGIN
BEGIN
	if 	(OLD.ie_suspenso = 'N' AND NEW.ie_suspenso = 'S') or (NEW.dt_fim 	<> OLD.dt_fim) then
		BEGIN
			reg_integracao_w.cd_estab_documento := wheb_usuario_pck.get_cd_estabelecimento;
			reg_integracao_w := gerar_int_padrao.gravar_integracao('306', NEW.NR_PRESCRICAO, NEW.nm_usuario, reg_integracao_w, 'NR_PRESCRICAO=' || NEW.NR_PRESCRICAO || ';NR_SEQUENCIA=' || NEW.NR_SEQUENCIA || ';DS_OPERACAO=UPDATE');
		END;	
	end if;
exception when others then
	null;
end;

  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_smart_jejum() FROM PUBLIC;

CREATE TRIGGER smart_jejum
