CREATE OR REPLACE FUNCTION trigger_fct_smart_pe_proced_aftinsupddel() RETURNS trigger AS $BODY$
declare

reg_integracao_w		gerar_int_padrao.reg_integracao;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
ie_integra_w			varchar(1) := 'N';
nr_seq_sae_proced_w		pe_procedimento.nr_sequencia%type;
nm_usuario_w			pe_procedimento.nm_usuario%type;
ie_evento_w				varchar(10) := '327';

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

If (NEW.ie_sistema_terceiro = 'S') then
	
	Select	OBTER_ESTABELECIMENTO_ATIVO
	into STRICT	cd_estabelecimento_w
	;
	
	if (TG_OP = 'DELETE') then
	
		nr_seq_sae_proced_w := OLD.NR_SEQUENCIA;
		nm_usuario_w 		:= OLD.nm_usuario;
	
	else
	
		nr_seq_sae_proced_w := NEW.NR_SEQUENCIA;
		nm_usuario_w 		:= NEW.nm_usuario;
	
	end if;
	
	Select 	Smart_obter_se_sae(nr_seq_sae_proced_w, cd_estabelecimento_w, ie_evento_w,'PE',nm_usuario_w)
	into STRICT	ie_integra_w
	;
	
	if (coalesce( ie_integra_w,'N') = 'S') then
	
		reg_integracao_w.cd_estab_documento	:=	cd_estabelecimento_w;
	
		if (TG_OP = 'INSERT') then
			BEGIN	
				reg_integracao_w := gerar_int_padrao.gravar_integracao(ie_evento_w, nr_seq_sae_proced_w, nm_usuario_w, reg_integracao_w, 'DS_OPERACAO_P=CREATE');
			END;	
		elsif (TG_OP = 'UPDATE') then
			BEGIN	
				if (NEW.IE_SITUACAO = 'I' and OLD.IE_SITUACAO <> 'I') then
					reg_integracao_w := gerar_int_padrao.gravar_integracao(ie_evento_w, nr_seq_sae_proced_w, nm_usuario_w, reg_integracao_w, 'DS_OPERACAO_P=DELETE');
				else
					reg_integracao_w := gerar_int_padrao.gravar_integracao(ie_evento_w, nr_seq_sae_proced_w, nm_usuario_w, reg_integracao_w, 'DS_OPERACAO_P=UPDATE');
				end if;
			END;
		elsif (TG_OP = 'DELETE') then
			BEGIN	
				reg_integracao_w := gerar_int_padrao.gravar_integracao(ie_evento_w, nr_seq_sae_proced_w, nm_usuario_w, reg_integracao_w, 'DS_OPERACAO_P=DELETE');
			END;
		end if;
	
	end if;

end if;

<<Final>>
ie_integra_w	:= 'N';

IF TG_OP = 'DELETE' THEN
	RETURN OLD;
ELSE
	RETURN NEW;
END IF;

end;
