CREATE OR REPLACE FUNCTION trigger_fct_smart_prescr_dieta_hor() RETURNS trigger AS $BODY$
declare
	reg_integracao_w		gerar_int_padrao.reg_integracao;
BEGIN
  BEGIN
BEGIN
	if 	(((OLD.DT_SUSPENSAO IS NULL AND NEW.DT_SUSPENSAO IS NOT NULL)
		OR ((OLD.DT_BLOQUEIO IS NULL)
			AND (NEW.DT_BLOQUEIO IS NOT NULL)
			OR (OLD.DT_BLOQUEIO IS NOT NULL)
			AND (NEW.DT_BLOQUEIO IS NULL))
		OR ((OLD.DT_DESBLOQUEIO_DIETA IS NULL)
			AND (NEW.DT_DESBLOQUEIO_DIETA IS NOT NULL)
			OR (OLD.DT_DESBLOQUEIO_DIETA IS NOT NULL)
			AND (NEW.DT_DESBLOQUEIO_DIETA IS NULL))
		OR (NEW.DT_HORARIO 	<> OLD.DT_HORARIO)
		AND (TG_OP = 'UPDATE'))
			OR  
		((coalesce(NEW.IE_APRAZADO,'N') = 'S') 
		and (TG_OP = 'INSERT')))
	then 
		BEGIN
			reg_integracao_w.cd_estab_documento := wheb_usuario_pck.get_cd_estabelecimento;	
			reg_integracao_w := gerar_int_padrao.gravar_integracao('320', NEW.NR_PRESCRICAO, NEW.nm_usuario, reg_integracao_w, 'NR_PRESCRICAO=' || NEW.NR_PRESCRICAO || ';NR_SEQUENCIA=' || NEW.NR_SEQ_DIETA || ';DS_OPERACAO=UPDATE');
		END;	
	end if;
exception when others then
	null;
end;

  END;
RETURN NEW;
END;
