DROP TRIGGER IF EXISTS smart_prescr_rec_hor ON prescr_rec_hor CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_smart_prescr_rec_hor() RETURNS trigger AS $BODY$
declare
	reg_integracao_w		gerar_int_padrao.reg_integracao;
BEGIN
  BEGIN
BEGIN
	if 	(((OLD.DT_SUSPENSAO IS NULL AND NEW.DT_SUSPENSAO IS NOT NULL)
	    OR (NEW.DT_HORARIO 	<> OLD.DT_HORARIO)
		AND (TG_OP = 'UPDATE')) 
			OR  
		((coalesce(NEW.IE_APRAZADO,'N') = 'S') 
		and (TG_OP = 'INSERT')))
		and (obter_se_envia_recomendacao(obter_estabelecimento_ativo, NEW.cd_recomendacao) = 'S')
	then 
		BEGIN
			reg_integracao_w.cd_estab_documento := wheb_usuario_pck.get_cd_estabelecimento;
			reg_integracao_w := gerar_int_padrao.gravar_integracao('300', NEW.NR_PRESCRICAO, NEW.nm_usuario, reg_integracao_w, 'NR_PRESCRICAO=' || NEW.NR_PRESCRICAO || ';NR_SEQUENCIA=' || NEW.NR_SEQ_RECOMENDACAO || ';DS_OPERACAO=UPDATE');
		END;	
	end if;
exception when others then
	null;
end;

  END;
RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_smart_prescr_rec_hor() FROM PUBLIC;

CREATE TRIGGER smart_prescr_rec_hor
	AFTER INSERT OR UPDATE ON prescr_rec_hor FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_smart_prescr_rec_hor();

