DROP TRIGGER IF EXISTS smart_procedimento ON prescr_procedimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_smart_procedimento() RETURNS trigger AS $BODY$
declare
	reg_integracao_w		gerar_int_padrao.reg_integracao;
BEGIN
  BEGIN
BEGIN
	if (OLD.DT_SUSPENSAO is null) and (NEW.DT_SUSPENSAO is not null) then
		BEGIN
		
			If (OLD.NR_SEQ_PROT_GLIC is not null) then -- Glicemia
				reg_integracao_w := gerar_int_padrao.gravar_integracao('328', NEW.NR_PRESCRICAO, NEW.nm_usuario, reg_integracao_w, 'NR_PRESCRICAO=' || NEW.NR_PRESCRICAO || ';NR_SEQUENCIA=' || NEW.NR_SEQUENCIA || ';DS_OPERACAO=UPDATE');
			ELSIF ((Obter_dados_proc_interno(NEW.nr_seq_proc_interno,'TU') = 'E') or (obter_cod_exame_lab(NEW.NR_SEQ_EXAME) IS NOT NULL)) then  -- Exames
				reg_integracao_w := gerar_int_padrao.gravar_integracao('310', NEW.NR_PRESCRICAO, NEW.nm_usuario, reg_integracao_w, 'NR_PRESCRICAO=' || NEW.NR_PRESCRICAO || ';NR_SEQUENCIA=' || NEW.NR_SEQUENCIA || ';DS_OPERACAO=UPDATE');
			end if;
		END;	
	end if;
exception when others then
	null;
end;

  END;
RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_smart_procedimento() FROM PUBLIC;

CREATE TRIGGER smart_procedimento
	AFTER UPDATE ON prescr_procedimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_smart_procedimento();

