SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS solic_compra_item_delete ON solic_compra_item CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_solic_compra_item_delete() RETURNS trigger AS $BODY$
declare

qt_registro_w		bigint;

BEGIN


select	count(*)
into STRICT	qt_registro_w
from	far_registro_falta
where	nr_solic_compra = OLD.nr_solic_compra
and	nr_item_solic_compra = OLD.nr_item_solic_compra;

if (qt_registro_w > 0) then

	update	far_registro_falta
	set	nr_solic_compra  = NULL,
		nr_item_solic_compra  = NULL
	where	nr_solic_compra = OLD.nr_solic_compra
	and	nr_item_solic_compra = OLD.nr_item_solic_compra;
end if;

select	count(*)
into STRICT	qt_registro_w
from	processo_aprov_compra
where	nr_sequencia = OLD.NR_SEQ_APROVACAO
and	coalesce(ie_aprov_reprov,'P') <> 'B';

if (qt_registro_w = 0) then

	update	processo_aprov_compra
	set	IE_APROV_REPROV = 'B'
	where	nr_sequencia = OLD.NR_SEQ_APROVACAO;

	delete from w_processo_aprov_compra
	where	nr_sequencia = OLD.NR_SEQ_APROVACAO;

end if;

RETURN OLD;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_solic_compra_item_delete() FROM PUBLIC;

CREATE TRIGGER solic_compra_item_delete
	BEFORE DELETE ON solic_compra_item FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_solic_compra_item_delete();

