CREATE OR REPLACE FUNCTION trigger_fct_solic_compra_item_entrega_tie() RETURNS trigger AS $BODY$
declare
  ds_retorno_integracao_w    text;
  json_w                     philips_json;
  json_data_w                text;
BEGIN
  if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') = 'S') then
    if (wheb_usuario_pck.get_nm_usuario is not null) then
      json_w := philips_json();

      json_w.put('request', NEW.NR_SOLIC_COMPRA);
      json_w.put('item', NEW.NR_ITEM_SOLIC_COMPRA);
      json_w.put('delivery', NEW.NR_ITEM_SOLIC_COMPRA_ENTR);
      json_w.put('deliveryAmount', NEW.QT_ENTREGA_SOLICITADA);
      json_w.put('deliveryDate', NEW.DT_ENTREGA_SOLICITADA);
      json_w.put('notes', NEW.DS_OBSERVACAO);
      json_w.put('lastUpdate', NEW.DT_ATUALIZACAO);
      json_w.put('lastUpdateBy', NEW.NM_USUARIO);

      dbms_lob.createtemporary(json_data_w, true);
      json_w.(json_data_w);

      ds_retorno_integracao_w := bifrost.send_integration_content('purchaserequestitemdelivery.send', json_data_w, wheb_usuario_pck.get_nm_usuario);

    end if;
  end if;
RETURN NEW;
end
