DROP TRIGGER IF EXISTS solic_compra_item_tie ON solic_compra_item CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_solic_compra_item_tie() RETURNS trigger AS $BODY$
declare
  ds_retorno_integracao_w    text;
  json_w                     philips_json;
  json_data_w                text;
BEGIN
  if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S') = 'S') then
    if (wheb_usuario_pck.get_nm_usuario is not null) then
      json_w := philips_json();

      json_w.put('request', NEW.NR_SOLIC_COMPRA);
      json_w.put('item', NEW.NR_ITEM_SOLIC_COMPRA);
      json_w.put('material', NEW.CD_MATERIAL);
      json_w.put('measurementUnit', NEW.CD_UNIDADE_MEDIDA_COMPRA);
      json_w.put('amount', NEW.QT_MATERIAL);
      json_w.put('settlementDate', NEW.DT_BAIXA);
      json_w.put('status', NEW.IE_SITUACAO);
      json_w.put('generate', NEW.IE_GERACAO);
      json_w.put('deliveryDate', NEW.DT_SOLIC_ITEM);
      json_w.put('authorizationDate', NEW.DT_AUTORIZACAO);
      json_w.put('disapprovalDate', NEW.DT_REPROVACAO);
      json_w.put('lastUpdate', NEW.DT_ATUALIZACAO);
      json_w.put('lastUpdateBy', NEW.NM_USUARIO);

      dbms_lob.createtemporary(json_data_w, true);
      json_w.(json_data_w);

      ds_retorno_integracao_w := bifrost.send_integration_content('purchaserequestitem.send', json_data_w, wheb_usuario_pck.get_nm_usuario);

    end if;
  end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
