DROP TRIGGER IF EXISTS subtipo_episodio_befins ON subtipo_episodio CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_subtipo_episodio_befins() RETURNS trigger AS $BODY$
declare
qt_reg_w	bigint;	

pragma autonomous_transaction;
BEGIN
  BEGIN

BEGIN
select	1
into STRICT	qt_reg_w
from	subtipo_episodio
where	nr_seq_tipo_episodio	= NEW.nr_seq_tipo_episodio
and	nr_seq_tipo_admissao	= NEW.nr_seq_tipo_admissao
and	nr_seq_tipo_admissao is not null
and	nr_sequencia	<> NEW.nr_sequencia  LIMIT 1;
exception
when others then
	qt_reg_w := 0;
end;

if (qt_reg_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1077039); --Este tipo de visita/admissão já está relacionado a reste tipo de episódio.
end if;

  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
