SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS sumario_enfermagem_insert ON sumario_enfermagem CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_sumario_enfermagem_insert() RETURNS trigger AS $BODY$
declare

qtd_w bigint;
dt_entrada_w sumario_enfermagem.dt_entrada%type;
BEGIN
  BEGIN
	if (coalesce(wheb_usuario_pck.get_ie_executar_trigger, 'N') = 'S') then
		select 	count(*) qtd
		into STRICT 	qtd_w
		from 	sumario_enfermagem 
		where 	ie_sumario_tipo      = 1
		and	nr_atendimento       = NEW.nr_atendimento
		and 	ie_sumario_enf_lista = NEW.ie_sumario_enf_lista;

		if ( qtd_w = 0 ) then
			NEW.ie_sumario_tipo := 1;
		else
			NEW.ie_sumario_tipo := 2;
		end if;			
	end if;

	BEGIN
		select a.dt_entrada
		  into STRICT dt_entrada_w
		  from atendimento_paciente a
		 where a.nr_atendimento = NEW.nr_atendimento;
		NEW.dt_entrada := dt_entrada_w;
		exception
			when no_data_found then
				dt_entrada_w := null;
	end;
  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_sumario_enfermagem_insert() FROM PUBLIC;

CREATE TRIGGER sumario_enfermagem_insert
	BEFORE INSERT ON sumario_enfermagem FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_sumario_enfermagem_insert();

