SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS sup_ajuste_est_cons_mat_atual ON sup_ajuste_est_cons_mat CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_sup_ajuste_est_cons_mat_atual() RETURNS trigger AS $BODY$
declare
ie_consignado_w		varchar(1);
cd_estabelecimento_w	smallint;
ie_valorizacao_consig_w	varchar(15);

BEGIN
if (NEW.cd_material is not null) then
	BEGIN
	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	sup_ajuste_estoque_consumo
	where	nr_sequencia = NEW.nr_seq_ajuste;

	select	coalesce(max(ie_valorizacao_consig),'N')
	into STRICT	ie_valorizacao_consig_w
	from	parametro_estoque
	where	cd_estabelecimento = cd_estabelecimento_w;

	select	ie_consignado
	into STRICT	ie_consignado_w
	from	material
	where	cd_material = NEW.cd_material;

	if	((ie_consignado_w = 1) or (ie_consignado_w = 2 AND NEW.cd_fornecedor is not null)) and (ie_valorizacao_consig_w <> 'CM') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266270);
		--'Neste estabelecimento não é apurado o custo médio de consignados, desta forma não é possível realizar o ajuste para   este item!');
	elsif (ie_consignado_w = 0) and (NEW.cd_fornecedor is not null) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266272);
		--'Para materiais do tipo normal não pode ser informado o fornecedor!');
	elsif (ie_consignado_w = 1) and (NEW.cd_fornecedor is null) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266273);
		--'Para materiais do tipo consignado é obrigatório informar o fornecedor!');
	end if;
	end;
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_sup_ajuste_est_cons_mat_atual() FROM PUBLIC;

CREATE TRIGGER sup_ajuste_est_cons_mat_atual
	BEFORE INSERT OR UPDATE ON sup_ajuste_est_cons_mat FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_sup_ajuste_est_cons_mat_atual();

