SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS sus_aih_reg_civil_atual ON sus_aih_reg_civil CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_sus_aih_reg_civil_atual() RETURNS trigger AS $BODY$
DECLARE
 
nr_sequencia_w		bigint;
nr_sequencia_ww		bigint;
nr_atendimento_w		bigint;
dt_entrada_w			timestamp;
dt_alta_w			timestamp;
qt_reg_w			bigint;
 
BEGIN 
 
select	coalesce(min(nr_atendimento),0) 
into STRICT	nr_atendimento_w 
from	sus_aih a 
where	a.nr_aih		= NEW.nr_aih;
 
select	coalesce(min(nr_sequencia),0) 
into STRICT	nr_sequencia_w 
from	procedimento_paciente 
where	nr_atendimento	= nr_atendimento_w 
and	ie_origem_proced	= 2 
and	cd_procedimento	in ( 
	35001011,35006013,35007010,35023015,35027010,35025018,35080019, 35021012,35024011, 
	35009012,35022019,35026014,35028017,35082011,35084014,35083018,35085010);
 
select	count(*) 
into STRICT	qt_reg_w 
from	procedimento_paciente 
where	nr_atendimento	= nr_atendimento_w 
and	cd_procedimento	= 99085011 
and	ie_origem_proced	= 2;
 
select	max(trunc(dt_entrada,'dd')), 
	max(coalesce(dt_alta, LOCALTIMESTAMP + interval '300 days')) 
into STRICT	dt_entrada_w, 
	dt_alta_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_w;
 
if (NEW.dt_nascimento	< dt_entrada_w) or (NEW.dt_nascimento > dt_alta_w) then 
	--r.aise_application_error(-20011,'A data de nascimento nÃ£o pode ser menor que a entrada ou maior que alta'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263433);
end if;
 
if (nr_sequencia_w > 0) and (qt_reg_w = 0) then 
	nr_sequencia_ww := Duplicar_proc_paciente(nr_sequencia_w, NEW.nm_usuario, nr_sequencia_ww);
	update	procedimento_paciente 
	set	cd_procedimento	= 99085011, 
		cd_medico_executor	 = NULL, 
		ie_tipo_servico_sus	= 36, 
		ie_tipo_ato_sus	= 46 
	where	nr_sequencia		= nr_sequencia_ww;
	CALL Atualiza_Preco_Proc_Sus(nr_sequencia_ww, NEW.nm_usuario); /*Felipe OS 27032 15/12/2005*/
end if;
 
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_sus_aih_reg_civil_atual() FROM PUBLIC;

CREATE TRIGGER sus_aih_reg_civil_atual
	AFTER INSERT OR UPDATE ON sus_aih_reg_civil FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_sus_aih_reg_civil_atual();

