DROP TRIGGER IF EXISTS sus_aih_unifafterpost ON sus_aih_unif CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_sus_aih_unifafterpost() RETURNS trigger AS $BODY$
declare


nr_sequencia_w		bigint;


BEGIN

if (NEW.nr_interno_conta <> OLD.nr_interno_conta) or (NEW.nr_interno_conta is null) then

	select	nextval('aih_hist_conta_seq')
	into STRICT	nr_sequencia_w
	;

	insert into aih_hist_conta(
		nr_sequencia,
		nr_aih,
		nr_seq_aih,
		nr_atendimento,
		nr_interno_conta,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_observacao)
	values (	nr_sequencia_w,
		NEW.nr_aih,
		NEW.nr_sequencia,
		NEW.nr_atendimento,
		NEW.nr_interno_conta,
		LOCALTIMESTAMP,
		NEW.nm_usuario,
		LOCALTIMESTAMP,
		NEW.nm_usuario,
		wheb_mensagem_pck.get_texto(310575));--substr('Alterado ou removido o v√≠nculo com conta paciente',1,2000));
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
