declare

cd_convenio_w	integer;

BEGIN
cd_convenio_w	:= 0;
/* andré - rotina comentada os82628/82652/82645 pois ao criar rotina de duplicação de apac era apresentado erro de mutante
if	(:new.nr_interno_conta is null) and
	(:old.nr_interno_conta is null) then
	select	max(a.cd_convenio)
	into	cd_convenio_w
	from	atend_categoria_convenio	a,
		convenio			c
	where	c.cd_convenio		= a.cd_convenio
	and	c.ie_tipo_convenio	= 3
	and	a.nr_atendimento	= :new.nr_atendimento;

	select	nvl(max(nr_interno_conta),null)
	into	:new.nr_interno_conta
	from 	conta_paciente
	where 	nr_atendimento 		= :new.nr_atendimento
	and	cd_convenio_parametro	= cd_convenio_w
	and	ie_status_acerto	= 1;
end if;
*/
if (coalesce(NEW.nr_interno_conta,0) > 0) and (coalesce(NEW.nr_apac,0) > 0) then
	BEGIN

	update	sus_laudo_paciente
	set	nr_interno_conta	= NEW.nr_interno_conta
	where	coalesce(nr_apac,0)	= NEW.nr_apac
	and	nr_atendimento	= NEW.nr_atendimento;

	end;
end if;

RETURN NEW;
end
