CREATE OR REPLACE FUNCTION trigger_fct_sus_bpa_unif_atual() RETURNS trigger AS $BODY$
DECLARE

cd_convenio_w		bigint;
nr_atendimento_w	bigint;
ds_module_w 		varchar(255);
osuser_w		varchar(100);

BEGIN

if (NEW.nr_interno_conta is not null) then
	select	nr_atendimento
	into STRICT	nr_atendimento_w
	from	conta_paciente
	where	nr_interno_conta	= NEW.nr_interno_conta;

	if (nr_atendimento_w	<> NEW.nr_atendimento) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(233472);
		/*Não é possivel vincular uma BPA em uma conta de outro atendimento!*/

	end if;
end if;

RETURN NEW;
END
