SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS sus_laudo_paciente_befins ON sus_laudo_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_sus_laudo_paciente_befins() RETURNS trigger AS $BODY$
DECLARE

ie_permite_w		varchar(1) := 'S';
ds_retorno_w		varchar(255)	:= '';
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
BEGIN
  BEGIN

if (NEW.nr_seq_interno is null) then
	select	nextval('sus_laudo_paciente_seq')
	into STRICT	NEW.nr_seq_interno
	;
end if;

select	Obter_Se_solic_laudo(NEW.nr_atendimento,NEW.ie_classificacao,coalesce(NEW.cd_medico_requisitante,NEW.cd_profis_requisitante))
into STRICT	ie_permite_w
;

if (ie_permite_w = 'N') then
	--'O medico requisitante nao tem permissao para gerar laudo. Verificar regra solicitantes de laudos nos Cadastros gerais/Aplicacao principal/Cadastros SUS.');

	CALL wheb_mensagem_pck.exibir_mensagem_abort(263527);
end if;

BEGIN
cd_estabelecimento_w := coalesce(wheb_usuario_pck.get_cd_estabelecimento,0);
exception
when others then
	cd_estabelecimento_w := 0;
end;

ds_retorno_w := sus_consiste_regra_laudo(cd_estabelecimento_w, NEW.cd_procedimento_solic, NEW.ie_origem_proced, ds_retorno_w);

if (coalesce(ds_retorno_w,'X') <> 'X') then
	ds_retorno_w := substr(substr(ds_retorno_w,1,220) || chr(13) || chr(10) || WHEB_MENSAGEM_PCK.get_texto(454285),1,255);
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262328 , 'DS_MENSAGEM='||ds_retorno_w);
end if;

  END;
RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_sus_laudo_paciente_befins() FROM PUBLIC;

CREATE TRIGGER sus_laudo_paciente_befins
	BEFORE INSERT ON sus_laudo_paciente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_sus_laudo_paciente_befins();

