SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS tabela_crud_param_atual ON tabela_crud_param CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_tabela_crud_param_atual() RETURNS trigger AS $BODY$
declare

qt_records_w	bigint;

--Please, be careful
pragma autonomous_transaction;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then

	select	count(*)
	into STRICT	qt_records_w
	from	tabela_crud_param
	where	nm_tabela		= NEW.nm_tabela
	and	nr_seq_visao		= NEW.nr_seq_visao
	and	nr_seq_obj_schematic	= NEW.nr_seq_obj_schematic
	and	((cd_estabelecimento	= NEW.cd_estabelecimento)	or (cd_estabelecimento is null 	and NEW.cd_estabelecimento is null))
	and	((cd_perfil			= NEW.cd_perfil) 		or (cd_perfil is null 		and NEW.cd_perfil is null))
	and	((nm_usuario_param		= NEW.nm_usuario_param) 	or (nm_usuario_param is null 	and NEW.nm_usuario_param is null))
	and	nr_sequencia 		<> NEW.nr_sequencia;

	if (qt_records_w > 0) then

		--JÃ¡ existe uma regra cadastrada com esses atributos.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(991975);

	end if;

end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_tabela_crud_param_atual() FROM PUBLIC;

CREATE TRIGGER tabela_crud_param_atual
	BEFORE INSERT OR UPDATE ON tabela_crud_param FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_tabela_crud_param_atual();

