CREATE OR REPLACE FUNCTION trigger_fct_tabela_parametro_atual() RETURNS trigger AS $BODY$
declare

cd_tabela_param_new_w	integer;

BEGIN
select	max(x.cd_tabela_custo)
into STRICT	cd_tabela_param_new_w
