SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS tabela_sistema_regra_atual ON tabela_sistema_regra CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_tabela_sistema_regra_atual() RETURNS trigger AS $BODY$
declare

qt_records_w	bigint;

--Please, be careful
pragma autonomous_transaction;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then

	select	count(*)
	into STRICT	qt_records_w
	from	tabela_sistema_regra
	where	nm_tabela		= NEW.nm_tabela
	and	((cd_estabelecimento	= NEW.cd_estabelecimento)	or (cd_estabelecimento is null	and NEW.cd_estabelecimento is null))
	and	((cd_perfil			= NEW.cd_perfil)		or (cd_perfil is null 		and NEW.cd_perfil is null))
	and	((cd_funcao		= NEW.cd_funcao)		or (cd_funcao is null 	and NEW.cd_funcao is null))
	and	((nm_usuario_regra		= NEW.nm_usuario_regra)	or (nm_usuario_regra is null 	and NEW.nm_usuario_regra is null))
	and	((nr_seq_visao		= NEW.nr_seq_visao)	or (nr_seq_visao is null 	and NEW.nr_seq_visao is null))
	and	nr_sequencia 		<> NEW.nr_sequencia;

	if (qt_records_w > 0) then

		--JÃ¡ existe uma regra cadastrada com esses atributos.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(991975);

	end if;

end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_tabela_sistema_regra_atual() FROM PUBLIC;

CREATE TRIGGER tabela_sistema_regra_atual
	BEFORE INSERT OR UPDATE ON tabela_sistema_regra FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_tabela_sistema_regra_atual();

