DROP TRIGGER IF EXISTS terceiro_operacao_atual ON terceiro_operacao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_terceiro_operacao_atual() RETURNS trigger AS $BODY$
declare
nr_seq_terceiro_w	bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
if (NEW.nr_seq_conta is not null) then
	select	max(nr_seq_terceiro)
	into STRICT	nr_seq_terceiro_w
	from	terceiro_conta
	where	nr_sequencia	= NEW.nr_seq_conta;

	if (nr_seq_terceiro_w <> NEW.nr_seq_terceiro) then
		-- A conta do terceiro selecionado e diferente do terceiro da operacao!

		CALL wheb_mensagem_pck.exibir_mensagem_abort(267024);
	end if;
end if;
end if;

RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_terceiro_operacao_atual() FROM PUBLIC;

CREATE TRIGGER terceiro_operacao_atual
	BEFORE INSERT OR UPDATE ON terceiro_operacao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_terceiro_operacao_atual();

