CREATE OR REPLACE FUNCTION trigger_fct_teste_funcao_item_acao_atual() RETURNS trigger AS $BODY$
BEGIN

update	teste_funcao a
set	dt_atualizacao	= LOCALTIMESTAMP,
	nm_usuario	= NEW.nm_usuario
where	a.nr_sequencia	= (	SELECT	nr_seq_teste
				from	TESTE_FUNCAO_ITEM x
