CREATE OR REPLACE FUNCTION trigger_fct_teste_func_item_result_atual() RETURNS trigger AS $BODY$
BEGIN

update	teste_funcao a
set	dt_atualizacao	= LOCALTIMESTAMP,
	nm_usuario	= NEW.nm_usuario
where	a.nr_sequencia	= (	SELECT	nr_seq_teste
				from	TESTE_FUNCAO_ITEM x
				where	x.nr_sequencia = (	select	NR_SEQ_ITEM
								from	TESTE_FUNCAO_ITEM_ACAO y
								where	y.nr_sequencia = NEW.NR_SEQ_ACAO));

RETURN NEW;
end
