DROP TRIGGER IF EXISTS tipo_acomodacao_atual ON tipo_acomodacao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_tipo_acomodacao_atual() RETURNS trigger AS $BODY$
declare
ie_origem_proced_w	bigint;

BEGIN

if (NEW.cd_procedimento is not null) and (NEW.ie_origem_proced is null) then
	BEGIN
	select	coalesce(min(ie_origem_proced), 0)
	into STRICT	ie_origem_proced_w
	from	procedimento
	where	cd_procedimento		= NEW.cd_procedimento;

	if (ie_origem_proced_w = 0) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(279710);
	end if;

	NEW.ie_origem_proced		:= ie_origem_proced_w;

	end;

end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_tipo_acomodacao_atual() FROM PUBLIC;

CREATE TRIGGER tipo_acomodacao_atual
	BEFORE INSERT OR UPDATE ON tipo_acomodacao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_tipo_acomodacao_atual();

