SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_pagar_after_update ON titulo_pagar CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_pagar_after_update() RETURNS trigger AS $BODY$
declare

qt_reg_w		bigint;
reg_integracao_w		gerar_int_padrao.reg_integracao;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
/*OS 1430325 - Envio de classificacao de titulos a pagar*/


select  count(*)
into STRICT    qt_reg_w
from    intpd_fila_transmissao
where   nr_seq_documento                = to_char(NEW.nr_titulo)
and     to_char(dt_atualizacao, 'dd/mm/yyyy hh24:mi:ss') = to_char(LOCALTIMESTAMP,'dd/mm/yyyy hh24:mi:ss')
and		ie_evento in ('53','179');

if (qt_reg_w = 0) then
	reg_integracao_w.cd_estab_documento    := NEW.cd_estabelecimento;
	reg_integracao_w.ie_tipo_titulo_cpa    := NEW.ie_tipo_titulo;
	reg_integracao_w := gerar_int_padrao.gravar_integracao('53', NEW.nr_titulo, NEW.nm_usuario, reg_integracao_w);
	
	/*OS 1602222 - Enviar status do titulo quando for liquidado.*/


	if (NEW.ie_situacao = 'L') then
		reg_integracao_w := gerar_int_padrao.gravar_integracao('179', NEW.nr_titulo, NEW.nm_usuario, reg_integracao_w);
	end if;
end if;

/* Grava o agendamento da informacao para atualizacao do fluxo de caixa. */


CALL gravar_agend_fluxo_caixa(NEW.nr_titulo,null,'TP',coalesce(NEW.dt_vencimento_atual,NEW.dt_vencimento_original),'A',NEW.nm_usuario);
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_pagar_after_update() FROM PUBLIC;

CREATE TRIGGER titulo_pagar_after_update
	AFTER UPDATE ON titulo_pagar FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_pagar_after_update();

