SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_pagar_alt_valor_after ON titulo_pagar_alt_valor CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_pagar_alt_valor_after() RETURNS trigger AS $BODY$
declare

vl_movimento_w		ctb_documento.vl_movimento%type;
cd_estabelecimento_w	ctb_documento.cd_estabelecimento%type;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
vl_movimento_w:= coalesce(NEW.vl_alteracao,0) - coalesce(NEW.vl_anterior,0);

if (coalesce(vl_movimento_w,0) <> 0) then
	BEGIN
	
	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	titulo_pagar
	where	nr_titulo = NEW.nr_titulo;
	
	CALL ctb_concil_financeira_pck.ctb_gravar_documento(	cd_estabelecimento_w,
							NEW.dt_alteracao,
							7,
							NEW.nr_seq_trans_fin,
							15,
							NEW.nr_titulo,
							NEW.nr_sequencia,
							null,
							vl_movimento_w,
							'TITULO_PAGAR_ALT_VALOR',
							'VL_ALTERADO',
							NEW.nm_usuario);	
	end;
end if;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_pagar_alt_valor_after() FROM PUBLIC;

CREATE TRIGGER titulo_pagar_alt_valor_after
	AFTER INSERT ON titulo_pagar_alt_valor FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_pagar_alt_valor_after();

