SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_pagar_cedente_insert ON titulo_pagar_cedente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_pagar_cedente_insert() RETURNS trigger AS $BODY$
DECLARE
ie_situacao_w		varchar(1);
dt_liquidacao_w		timestamp;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
select	ie_situacao,
	dt_liquidacao
into STRICT	ie_situacao_w,
	dt_liquidacao_w
from	titulo_pagar
where	nr_titulo = NEW.nr_titulo;

if 	(NEW.cd_pessoa_fisica is null AND NEW.cd_cgc is null) or
	(NEW.cd_pessoa_fisica is not null AND NEW.cd_cgc is not null) then
	-- O titulo deve ter apenas um cedente!

	CALL wheb_mensagem_pck.exibir_mensagem_abort(267029);

elsif (ie_situacao_w <> 'A') or (dt_liquidacao_w is not null) then
	-- O titulo deve estar aberto!

	CALL wheb_mensagem_pck.exibir_mensagem_abort(267030);

else
	update	titulo_pagar
	set	cd_pessoa_fisica 	= NEW.cd_pessoa_fisica,
		cd_cgc 			= NEW.cd_cgc,
		nm_usuario		= NEW.nm_usuario,
		dt_atualizacao		= LOCALTIMESTAMP
	where	nr_titulo 		= NEW.nr_titulo;
end if;
end if;

RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_pagar_cedente_insert() FROM PUBLIC;

CREATE TRIGGER titulo_pagar_cedente_insert
	BEFORE INSERT ON titulo_pagar_cedente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_pagar_cedente_insert();

