SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_pagar_imposto_afatual ON titulo_pagar_imposto CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_pagar_imposto_afatual() RETURNS trigger AS $BODY$
declare

qt_reg_w 		bigint;
reg_integracao_w		gerar_int_padrao.reg_integracao;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

/*Esse select e para tentar evitar a duplicidade de envio para intpd_fila_transmissao*/


select  count(*)
into STRICT    qt_reg_w
from    intpd_fila_transmissao
where   nr_seq_documento                = to_char(NEW.nr_titulo)
and     to_char(dt_atualizacao, 'dd/mm/yyyy hh24:mi:ss') = to_char(LOCALTIMESTAMP,'dd/mm/yyyy hh24:mi:ss')
and		ie_evento in ('53');

/*OS 1430325 - Envio de classificacao de titulos a pagar*/


if (qt_reg_w = 0) then
	select  max(cd_estabelecimento),
			max(ie_tipo_titulo)
			into STRICT reg_integracao_w.cd_estab_documento,
			     reg_integracao_w.ie_tipo_titulo_cpa
			from titulo_pagar
			where nr_titulo = NEW.nr_titulo;
	reg_integracao_w := gerar_int_padrao.gravar_integracao('53', NEW.nr_titulo, NEW.nm_usuario, reg_integracao_w);
end if;	
end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_pagar_imposto_afatual() FROM PUBLIC;

CREATE TRIGGER titulo_pagar_imposto_afatual
	AFTER INSERT ON titulo_pagar_imposto FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_pagar_imposto_afatual();

