SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_pagar_imposto_bfupdate ON titulo_pagar_imposto CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_pagar_imposto_bfupdate() RETURNS trigger AS $BODY$
declare
cd_estabelecimento_w		ctb_documento.cd_estabelecimento%type;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	titulo_pagar
where	nr_titulo = NEW.nr_titulo;

/* Projeto Davita  - Nao deixa gerar movimento contabil apos fechamento  da data */


CALL philips_contabil_pck.valida_se_dia_fechado(OBTER_EMPRESA_ESTAB(cd_estabelecimento_w),NEW.dt_contabil);

if (OLD.ds_stack is null) then
	NEW.ds_stack := substr('CallStack Update: ' || substr(dbms_utility.format_call_stack,1,3980),1,4000);
end if;

if (NEW.nr_seq_pls_venc_trib is not null) and (OLD.dt_imposto <> NEW.dt_imposto) then
	update	pls_pag_prest_venc_trib
	set	dt_imposto	= NEW.dt_imposto
	where	nr_sequencia	= NEW.nr_seq_pls_venc_trib;
end if;
end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_pagar_imposto_bfupdate() FROM PUBLIC;

CREATE TRIGGER titulo_pagar_imposto_bfupdate
	BEFORE UPDATE ON titulo_pagar_imposto FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_pagar_imposto_bfupdate();

