SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_receber_classif_integr ON titulo_receber_classif CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_receber_classif_integr() RETURNS trigger AS $BODY$
declare

qt_reg_w	bigint;
reg_integracao_w		gerar_int_padrao.reg_integracao;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
if (NEW.nr_titulo is not null) then

	/*Esse select e para tentar evitar a duplicidade. Pois ao ao atualizar algo no titulo, pode chamar outra proc que atualiza classificacao ou imposto, que tb dispara a trigger das tabelas com esse insert*/


	select	count(*)
	into STRICT	qt_reg_w
	from	intpd_fila_transmissao
	where  	nr_seq_documento 		= to_char(NEW.nr_titulo)
	and     to_char(dt_atualizacao, 'dd/mm/yyyy hh24:mi:ss') = to_char(LOCALTIMESTAMP,'dd/mm/yyyy hh24:mi:ss');
	
	if (qt_reg_w = 0) then

		reg_integracao_w := gerar_int_padrao.gravar_integracao('26', NEW.nr_titulo, NEW.nm_usuario, reg_integracao_w);
		
	end if;

end if;

if (TG_OP = 'INSERT') then
	/* Grava o agendamento da informacao para atualizacao do fluxo de caixa. */


	CALL gravar_agend_fluxo_caixa(NEW.nr_titulo,null,'TR',NEW.dt_atualizacao,'I',NEW.nm_usuario);
elsif (TG_OP = 'UPDATE') then
	/* Grava o agendamento da informacao para atualizacao do fluxo de caixa. */


	CALL gravar_agend_fluxo_caixa(NEW.nr_titulo,null,'TR',NEW.dt_atualizacao,'A',NEW.nm_usuario);
end if;
end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_receber_classif_integr() FROM PUBLIC;

CREATE TRIGGER titulo_receber_classif_integr
	AFTER INSERT OR UPDATE ON titulo_receber_classif FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_receber_classif_integr();

