SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_receber_cobr_after_del ON titulo_receber_cobr CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_receber_cobr_after_del() RETURNS trigger AS $BODY$
declare

qt_reg_w		bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

/*Verificar se esse titulo que foi excluido do lote ja tinha sido gerado nele atraves da TITULO_RECEBER_INSTR*/


select	count(*)
into STRICT	qt_reg_w
from	titulo_receber_instr
where	nr_titulo				= OLD.nr_titulo
and		cd_ocorrencia			= OLD.cd_ocorrencia
and		ie_instrucao_enviada	= 'S';

/*Se existir esse titulo na TITULO_RECEBER_INSTR com instrucao ja enviada, atualizar ele para N no instrucao enviada para que possa ser selcionado novamente em lotes futuros*/


if (qt_reg_w > 0) then

	update	titulo_receber_instr a
	set 	a.ie_instrucao_enviada = 'N'
	where 	a.nr_titulo 			= OLD.nr_titulo
	and 	a.cd_ocorrencia 		= OLD.cd_ocorrencia
	and		a.ie_instrucao_enviada 	= 'S';
	
end if;	
end if;	
RETURN OLD;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_receber_cobr_after_del() FROM PUBLIC;

CREATE TRIGGER titulo_receber_cobr_after_del
	AFTER DELETE ON titulo_receber_cobr FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_receber_cobr_after_del();

