SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_receber_cobr_bef_ins ON titulo_receber_cobr CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_receber_cobr_bef_ins() RETURNS trigger AS $BODY$
declare

vl_saldo_titulo_w		double precision := 0;
vl_multa_w			double precision := 0;
vl_juros_w			double precision := 0;
nr_seq_conta_banco_w		bigint;
ie_cobr_juro_multa_calc_w	varchar(1) := 'N';
ie_juros_multa_barras_bloq_w	varchar(3) := 'N';
vl_juros_boleto_w		double precision := null;
vl_multa_boleto_w		double precision := null;
cd_estabelecimento_w		smallint;
cd_estab_titulo_w		smallint;
cd_estab_financ_w		smallint;
cd_estab_ativo_w		smallint;
ie_permite_estab_w		varchar(1);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
if (NEW.dt_liquidacao is not null) then
	NEW.vl_multa_calc	:= obter_juros_multa_titulo(NEW.nr_titulo, NEW.dt_liquidacao, 'R', 'M');
	NEW.vl_juros_calc	:= obter_juros_multa_titulo(NEW.nr_titulo, NEW.dt_liquidacao, 'R', 'J');
end if;

cd_estab_ativo_w := obter_estabelecimento_ativo;
ie_permite_estab_w := obter_param_usuario(815, 47, obter_perfil_ativo, NEW.nm_usuario, cd_estab_ativo_w, ie_permite_estab_w);

select	max(nr_seq_conta_banco)
into STRICT	nr_seq_conta_banco_w
from	cobranca_escritural
where	nr_sequencia	= NEW.nr_seq_cobranca;
	
select	max(ie_cobr_juro_multa_calc)
into STRICT	ie_cobr_juro_multa_calc_w
from	banco_estabelecimento
where	nr_sequencia		= nr_seq_conta_banco_w;

if (coalesce(ie_cobr_juro_multa_calc_w,'N') = 'G') then
	select	max(vl_saldo_titulo),
		max(cd_estabelecimento)
	into STRICT	vl_saldo_titulo_w,
		cd_estabelecimento_w
	from	titulo_receber
	where	nr_titulo = NEW.nr_titulo;
	
	select	coalesce(max(ie_juros_multa_barras_bloq),'N')
	into STRICT	ie_juros_multa_barras_bloq_w
	from	parametro_contas_receber a
	where	a.cd_estabelecimento	= cd_estabelecimento_w;
	
	if (ie_juros_multa_barras_bloq_w <> 'N') then
		select	a.vl_juros_boleto,
			a.vl_multa_boleto
		into STRICT	vl_juros_boleto_w,
			vl_multa_boleto_w
		from	titulo_receber a
		where	a.nr_titulo	= NEW.nr_titulo;
	end if;
	
	vl_multa_w := coalesce(obter_juros_multa_titulo(NEW.nr_titulo, NEW.dt_liquidacao, 'R', 'M'),0);
	vl_juros_w := coalesce(obter_juros_multa_titulo(NEW.nr_titulo, NEW.dt_liquidacao, 'R', 'J'),0);
	
	-- BOLETO

	if (vl_juros_boleto_w > 0) or (vl_multa_boleto_w > 0) then
		vl_juros_w	:= coalesce(vl_juros_boleto_w,0);
		vl_multa_w	:= coalesce(vl_multa_boleto_w,0);
	end if;

	if (coalesce(vl_saldo_titulo_w,0) > 0) and (NEW.vl_liquidacao > coalesce(vl_saldo_titulo_w,0)) and (coalesce(NEW.vl_multa,0) = 0) and (coalesce(NEW.vl_juros,0) = 0) and (vl_multa_w > 0) then
		NEW.vl_multa := vl_multa_w;
		vl_saldo_titulo_w := coalesce(vl_saldo_titulo_w,0) + coalesce(NEW.vl_multa,0);
	end if;
	
	if (coalesce(vl_saldo_titulo_w,0) > 0) and (NEW.vl_liquidacao > coalesce(vl_saldo_titulo_w,0)) and (coalesce(NEW.vl_juros,0) = 0) and (vl_juros_w > 0) then
		NEW.vl_juros := NEW.vl_liquidacao - coalesce(vl_saldo_titulo_w,0);
		
		if (coalesce(vl_juros_boleto_w,0) > 0) then
			NEW.vl_juros := vl_juros_boleto_w;
		end if;
	end if;
end if;

if (coalesce(ie_permite_estab_w,'S') = 'N') then
	select	max(cd_estabelecimento),
		max(cd_estab_financeiro)
	into STRICT	cd_estab_titulo_w,
		cd_estab_financ_w
	from	titulo_receber
	where	nr_titulo = NEW.nr_titulo;
	if (cd_estab_titulo_w <> cd_estab_ativo_w AND cd_estab_financ_w <> cd_estab_ativo_w) then
		-- Nao e permitido incluir titulos de outro estabelecimento. Parametro [47]

		CALL wheb_mensagem_pck.exibir_mensagem_abort(446941);
	end if;
end if;
end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_receber_cobr_bef_ins() FROM PUBLIC;

CREATE TRIGGER titulo_receber_cobr_bef_ins
	BEFORE INSERT ON titulo_receber_cobr FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_receber_cobr_bef_ins();

