SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_receber_cobr_update ON titulo_receber_cobr CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_receber_cobr_update() RETURNS trigger AS $BODY$
declare

cd_estab_titulo_w		smallint;
cd_estab_financ_w		smallint;
cd_estab_ativo_w		smallint;
ie_permite_estab_w		varchar(1);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
cd_estab_ativo_w := obter_estabelecimento_ativo;
ie_permite_estab_w := obter_param_usuario(815, 47, obter_perfil_ativo, NEW.nm_usuario, cd_estab_ativo_w, ie_permite_estab_w);

if (coalesce(ie_permite_estab_w,'S') = 'N') then
	select	max(cd_estabelecimento),
		max(cd_estab_financeiro)
	into STRICT	cd_estab_titulo_w,
		cd_estab_financ_w
	from	titulo_receber
	where	nr_titulo = NEW.nr_titulo;
	if (cd_estab_titulo_w <> cd_estab_ativo_w AND cd_estab_financ_w <> cd_estab_ativo_w) then
		-- Nao e permitido incluir titulos de outro estabelecimento. Parametro [47]

		CALL wheb_mensagem_pck.exibir_mensagem_abort(446941);
	end if;
end if;
end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_receber_cobr_update() FROM PUBLIC;

CREATE TRIGGER titulo_receber_cobr_update
	BEFORE UPDATE ON titulo_receber_cobr FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_receber_cobr_update();

