SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_receber_integr ON titulo_receber CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_receber_integr() RETURNS trigger AS $BODY$
declare

qt_reg_w	bigint;
reg_integracao_p		gerar_int_padrao.reg_integracao;

BEGIN

if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,'S')	= 'S')  then

if (NEW.nr_titulo is not null) then

	/*Esse select é para tentar evitar a duplicidade. Pois ao ao atualizar algo no titulo, pode chamar outra proc que atualiza classificacao ou imposto, que tb dispara a trigger das tabelas com esse insert*/


	select	count(*)
	into STRICT	qt_reg_w
	from	intpd_fila_transmissao
	where  	nr_seq_documento 		= to_char(NEW.nr_titulo)
	and     to_char(dt_atualizacao, 'dd/mm/yyyy hh24:mi:ss') = to_char(LOCALTIMESTAMP,'dd/mm/yyyy hh24:mi:ss');
	
	if (qt_reg_w = 0) then
	    /*Envio de cadastros financeiros - Geração de título*/


		reg_integracao_p := gerar_int_padrao.gravar_integracao('31', NEW.nr_titulo, NEW.nm_usuario, reg_integracao_p);
		
		reg_integracao_p.cd_estab_documento    := NEW.cd_estabelecimento;
		reg_integracao_p.ie_origem_titulo_cre  := NEW.ie_origem_titulo;
		reg_integracao_p.ie_tipo_titulo_cre    := NEW.ie_tipo_titulo;
		reg_integracao_p := gerar_int_padrao.gravar_integracao('26', NEW.nr_titulo, NEW.nm_usuario, reg_integracao_p);
		
	end if;

end if;

end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_receber_integr() FROM PUBLIC;

CREATE TRIGGER titulo_receber_integr
	AFTER INSERT OR UPDATE ON titulo_receber FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_receber_integr();

