SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_receber_liq_delete ON titulo_receber_liq CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_receber_liq_delete() RETURNS trigger AS $BODY$
DECLARE

dt_fechamento_w			timestamp	:= null;
nr_recibo_w			bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
if (OLD.nr_seq_caixa_rec is not null) then
	select	max(dt_fechamento),
		max(nr_recibo)
	into STRICT	dt_fechamento_w,
		nr_recibo_w
	from	caixa_receb
	where	nr_sequencia	= OLD.nr_seq_caixa_rec;

	if (dt_fechamento_w is not null) then
		/* Nao e possivel excluir a baixa.
		O recibo #@NR_RECIBO#@ ja foi fechado!*/

		CALL wheb_mensagem_pck.exibir_mensagem_abort(267076, 'NR_RECIBO=' || nr_recibo_w);
	end if;
end if;	
/* Grava o agendamento da informacao para atualizacao do fluxo de caixa. */


CALL gravar_agend_fluxo_caixa(OLD.nr_titulo,OLD.nr_sequencia,'TRB',OLD.dt_recebimento,'E',OLD.nm_usuario);
/* Quando houver vl_recurso, gera informacao de glosa a recuperar */


if (coalesce(OLD.vl_recurso,0) <> 0) then
	CALL gravar_agend_fluxo_caixa(OLD.nr_titulo,null,'TR',OLD.dt_recebimento,'E',OLD.nm_usuario);
end if;

end if;

RETURN OLD;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_receber_liq_delete() FROM PUBLIC;

CREATE TRIGGER titulo_receber_liq_delete
	BEFORE DELETE ON titulo_receber_liq FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_receber_liq_delete();

