SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS titulo_receber_liq_integr_afte ON titulo_receber_liq CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_titulo_receber_liq_integr_afte() RETURNS trigger AS $BODY$
declare

reg_integracao_p		gerar_int_padrao.reg_integracao;
qt_reg_w				bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
if (NEW.nr_titulo is not null) and (NEW.nr_sequencia is not null) and ( coalesce(NEW.ie_lib_caixa,'S') = 'S') then

	/*Esse select e para tentar evitar a duplicidade. Pois ao ao atualizar algo no titulo, pode chamar outra proc que atualiza classificacao ou imposto, que tb dispara a trigger das tabelas com esse insert*/


	select	count(*)
	into STRICT	qt_reg_w
	from	intpd_fila_transmissao
	where  	nr_seq_documento 		= to_char(NEW.nr_titulo)
	and		nr_seq_item_documento	= to_char(NEW.nr_sequencia)
	and     to_char(dt_atualizacao, 'dd/mm/yyyy hh24:mi:ss') = to_char(LOCALTIMESTAMP,'dd/mm/yyyy hh24:mi:ss')
	and		ie_evento in ('32','27');
	
	if (qt_reg_w = 0) then
		reg_integracao_p.nr_seq_item_documento_p		:=	NEW.nr_sequencia;
		
		/*Envio de cadastros financeiros - Baixa de titulo*/


		reg_integracao_p := gerar_int_padrao.gravar_integracao('32', NEW.nr_titulo, NEW.nm_usuario, reg_integracao_p);
		
		reg_integracao_p := gerar_int_padrao.gravar_integracao('27', NEW.nr_titulo, NEW.nm_usuario, reg_integracao_p);
	end if;
end if;
/* Grava o agendamento da informacao para atualizacao do fluxo de caixa. */


CALL gravar_agend_fluxo_caixa(NEW.nr_titulo,NEW.nr_sequencia,'TRB',NEW.dt_recebimento,'A',NEW.nm_usuario);
/* Quando houver vl_recurso, gera informacao de glosa a recuperar */


if (coalesce(NEW.vl_recurso,0) <> 0) then
	CALL gravar_agend_fluxo_caixa(NEW.nr_titulo,null,'TR',NEW.dt_recebimento,'A',NEW.nm_usuario);
end if;

end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_titulo_receber_liq_integr_afte() FROM PUBLIC;

CREATE TRIGGER titulo_receber_liq_integr_afte
	AFTER UPDATE ON titulo_receber_liq FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_titulo_receber_liq_integr_afte();

